@@"},"$:/language/Snippets/list-tree":{"title":"$:/language/Snippets/list-tree","caption":"list-tree","tags":"$:/tags/TextEditor/Snippet"},"$:/xp/macros/annotation":{"title":"$:/xp/macros/annotation","tags":"$:/tags/Macro","text":"\define annotation-inner(storeTiddler)
\whitespace trim
<div class="tc-drop-down-wrapper">
<div class="xp-popup tc-popup-keep" style="max-width:550px;overflow-y:hidden;">
<div class="xp-annotation-controls">
<$button to=<<__storeTiddler__>> tooltip="Open this annotation" class="tc-btn-invisible">
<$action-deletetiddler $tiddler=<<popupstate>>/>
{{$:/core/images/link}}
</$button>
<$reveal state=<<editstate>> type="nomatch" text="yes">
<$button set=<<editstate>> setTo="yes" tooltip="Edit this annotation" class="tc-btn-invisible">
{{$:/core/images/edit-button}}
</$button>
</$reveal>
<$reveal state=<<editstate>> type="match" text="yes">
<$button set=<<editstate>> setTo="no" tooltip="Exit edit" class="tc-btn-invisible tc-selected">
{{$:/core/images/edit-button}}
</$button>
</$reveal>
</div>
<div class="xp-popup-text">
<$reveal state=<<editstate>> type="nomatch" text="yes">
<$transclude tiddler=<<__storeTiddler__>> mode="block"/>
</$reveal>
<$reveal state=<<editstate>> type="match" text="yes">
<$edit-text tiddler=<<__storeTiddler__>> tag="textarea" rows="15" class="xp-annotation-texteditor" focus="no"/>
</$reveal>
</div>
</div></div>
\end

\define annotation-hover(text,storeTiddler)
\whitespace trim
<div class="xp-annotation-hover"> 
<$button class="xp-annotation">
$text$
</$button>^^<span class="tc-btn-dropdown xp-image-annotation">{{$:/xp/images/annotation}}</span>^^
<div class="xp-annotation-hover-text">
<$macrocall
  $name="annotation-inner"
  storeTiddler=<<__storeTiddler__>>
/>
</div>
</div>
\end

\define annotation-click(text,storeTiddler)
\whitespace trim
<$button popup=<<popupstate>> class="xp-annotation">
$text$
</$button>^^<span class="tc-btn-dropdown xp-image-annotation">{{$:/xp/images/annotation}}</span>^^<$reveal type="popup" state=<<popupstate>> position="below" updatePopupPosition="yes" class="tc-popup-keep" animate="yes" retain="yes" style="overflow-y:hidden;">
<$macrocall
  $name="annotation-inner"
  storeTiddler=<<__storeTiddler__>>
/>
</$reveal>
\end

\define annotate(text,storeTiddler)
\whitespace trim
<$let popupstate={{{ [[$storeTiddler$]removeprefix[$:/annotation/]addprefix[$:/state/popup/annotation-]] }}} editstate={{{ [[$storeTiddler$]removeprefix[$:/annotation/]addprefix[$:/state/edit/annotation-]] }}}>
<$reveal state="$:/config/annotation/mode" type="match" text="hover" default="hover" animate="yes" retain="yes">
<$macrocall
  $name="annotation-hover"
  text=<<__text__>>
  storeTiddler=<<__storeTiddler__>>
/>
</$reveal>
<$reveal state="$:/config/annotation/mode" type="match" text="click">
<$macrocall
  $name="annotation-click"
  text=<<__text__>>
  storeTiddler=<<__storeTiddler__>>
/>
</$reveal>
</$let>
\end
"},"$:/xp/macros/list":{"title":"$:/xp/macros/list","tags":"$:/tags/Macro","text":"<!-- $:/xp/Stylesheet/expansion -->

\define list-expandable(tag,sort,prompt)
<div class="xp-list-expandable-wrapper">
<$reveal default=<<__prompt__>> type="nomatch" text="">
<div class="xp-expansion-prompt">$prompt$</div>
</$reveal>
<div class="tc-manager-list">
  <$list filter="[all[shadows+tiddlers]tag[$tag$]!has[draft.of]$sort$]">
    <$vars transclusion=<<currentTiddler>>>
      <div style="tc-manager-list-item">
        <$button popup=<<qualify "$:/state/list-expandable">> class="tc-btn-invisible tc-manager-list-item-heading" selectedClass="tc-manager-list-item-heading-selected">
          <$text text=<<currentTiddler>>/>
        </$button>
        <$reveal state=<<qualify "$:/state/list-expandable">> type="nomatch" text="" default="" tag="div" class="tc-popup-handle">
          <div class="xp-expansion-item-content">
            <$transclude mode="block"/>
          </div>
        </$reveal>
      </div>
    </$vars>
  </$list>
</div>
</div>
\end

\define list-questions(tag,sort,prompt)
<$reveal default=<<__prompt__>> type="nomatch" text="">
<div class="xp-expansion-prompt">$prompt$</div>
</$reveal>
<$list filter="[all[shadows+tiddlers]tag[$tag$]!has[draft.of]$sort$]">
  <$qualify name="question-state" title={{{ [[$:/state/question]addsuffix[-]addsuffix<currentTiddler>] }}}>
  <$reveal type="nomatch" stateTitle=<<question-state>> text="open">
    <$button setTitle=<<question-state>> setTo="open" class="tc-btn-invisible tc-popup-keep xp-expansion">
      <<toc-caption>>
      <span class="xp-expansion-chevron xp-chevron-down">{{$:/xp/images/chevron}}</span>
    </$button>
  </$reveal>
  <$reveal type="match" stateTitle=<<question-state>> text="open">
    <$button setTitle=<<question-state>> setTo="close" class="tc-btn-invisible tc-popup-keep xp-expansion" style="font-size:1.5em;">
      <$link to={{{ [<currentTiddler>get[target]else<currentTiddler>] }}}><$view field='caption'><$view field='title'/></$view></$link>
      <span class="xp-expansion-chevron xp-chevron-up">{{$:/xp/images/chevron}}</span>
    </$button>
  </$reveal>
  <$reveal type="match" stateTitle=<<question-state>> text="open">
    <div class="xp-expansion-text">
    <$transclude mode=block/>
    </div>
  </$reveal>
  </$qualify>
</$list>
<div class="xp-expansion-line"></div>
\end

\define list-questions-autoclose(tag,sort,prompt)
<$reveal default=<<__prompt__>> type="nomatch" text="">
<div class="xp-expansion-prompt">$prompt$</div>
</$reveal>
<$list filter="[all[shadows+tiddlers]tag[$tag$]!has[draft.of]$sort$]">
  <$qualify name="question-state" title={{{ [[$:/state/question]addsuffix[-]addsuffix<currentTiddler>] }}}>
  <$reveal type="nomatch" stateTitle=<<question-state>> text="open">
    <$button setTitle=<<question-state>> setTo="open" class="tc-btn-invisible tc-popup-keep xp-expansion">
      <$list filter="[[$:/config/question/first-open]!is[tiddler]]">
        <$action-setfield $tiddler="$:/config/question/first-open" text=<<question-state>>/>
      </$list>
      <$list filter="[[$:/config/question/first-open]is[tiddler]]">
        <$action-setfield $tiddler={{$:/config/question/first-open}} text="close"/>
        <$action-setfield $tiddler="$:/config/question/first-open" text=<<question-state>>/>
      </$list>
      <<toc-caption>>
      <span class="xp-expansion-chevron xp-chevron-down">{{$:/xp/images/chevron}}</span>
    </$button>
  </$reveal>
  <$reveal type="match" stateTitle=<<question-state>> text="open">
    <$button setTitle=<<question-state>> setTo="close" class="tc-btn-invisible tc-popup-keep xp-expansion" style="font-size:1.5em;">
      <$link to={{{ [<currentTiddler>get[target]else<currentTiddler>] }}}><$view field='caption'><$view field='title'/></$view></$link>
      <span class="xp-expansion-chevron xp-chevron-up">{{$:/xp/images/chevron}}</span>
    </$button>
  </$reveal>
  <$reveal type="match" stateTitle=<<question-state>> text="open">
    <div class="xp-expansion-text">
    <$transclude mode=block/>
    </div>
  </$reveal>
  </$qualify>
</$list>
<div class="xp-expansion-line"></div>
\end


\define list-questions-autoclose2(tag,sort,prompt)
<$reveal default=<<__prompt__>> type="nomatch" text="">
<div class="xp-expansion-prompt">$prompt$</div>
</$reveal>
<$list filter="[all[shadows+tiddlers]tag[$tag$]!has[draft.of]$sort$]">
  <$vars transclusion=<<currentTiddler>>>
  <$qualify name="question-state" title={{{ [[$:/state/question]addsuffix[-]addsuffix<currentTiddler>] }}}>
    <$button popup=<<qualify "$:/state/list-expandable">> class="tc-btn-invisible tc-popup-keep xp-expansion">
      <$reveal type="nomatch" stateTitle=<<question-state>> text="open">
        <$action-deletetiddler $filter="[prefix[$:/state/question-]] [prefix[$:/state/list-expandable]]"/>
        <$action-setfield $tiddler=<<question-state>> text="open"/>
      <$text text=<<currentTiddler>>/>
      <span class="xp-expansion-chevron xp-chevron-down">{{$:/xp/images/chevron}}</span>
      </$reveal>
      <$reveal type="match" stateTitle=<<question-state>> text="open">
        <$action-setfield $tiddler=<<question-state>> text="close"/>
        <$link to={{{ [<currentTiddler>get[target]else<currentTiddler>] }}}><$view field='caption'><$view field='title'/></$view></$link>
        <span class="xp-expansion-chevron xp-chevron-up">{{$:/xp/images/chevron}}</span>
      </$reveal>
    </$button>
    <$reveal state=<<qualify "$:/state/list-expandable">> type="nomatch" text="" default="" tag="div" class="tc-popup-handle">
      <div style="padding: 0 0 9px; vertical-align: middle;">
        <$transclude mode="block"/>
      </div>
    </$reveal>
  </$qualify>
  </$vars>
</$list>
<div class="xp-expansion-line"></div>
\end
